import{_ as s,C as e,c as u,o as c,af as p,G as r,j as l}from"./chunks/framework.CpuDhtGM.js";const P=JSON.parse('{"title":"白酒","description":"","frontmatter":{},"headers":[],"relativePath":"company/chinese-sprites/chinese-sprites.md","filePath":"company/chinese-sprites/chinese-sprites.md","lastUpdated":1765447822000}'),d={name:"company/chinese-sprites/chinese-sprites.md"};function m(h,i,b,f,_,q){const a=e("CompareDataTable"),o=e("ComparePrimaryBusinessTable"),t=e("CompareTurnoverRateTable"),n=e("CompareReturnTable");return c(),u("div",null,[i[0]||(i[0]=p('<h1 id="白酒" tabindex="-1">白酒 <a class="header-anchor" href="#白酒" aria-label="Permalink to &quot;白酒&quot;">​</a></h1><h2 id="综合信息" tabindex="-1">综合信息 <a class="header-anchor" href="#综合信息" aria-label="Permalink to &quot;综合信息&quot;">​</a></h2><h3 id="书籍推荐" tabindex="-1">书籍推荐 <a class="header-anchor" href="#书籍推荐" aria-label="Permalink to &quot;书籍推荐&quot;">​</a></h3><ul><li>《茅台大博弈》</li><li>《上瘾五百年》</li></ul><h3 id="博主推荐" tabindex="-1">博主推荐 <a class="header-anchor" href="#博主推荐" aria-label="Permalink to &quot;博主推荐&quot;">​</a></h3><ul><li>价投小白鸽（跟踪白酒动销 公众号/雪球）</li></ul><h3 id="文章推荐" tabindex="-1">文章推荐 <a class="header-anchor" href="#文章推荐" aria-label="Permalink to &quot;文章推荐&quot;">​</a></h3><ul><li><a href="https://mp.weixin.qq.com/s/xWjIejoMeVcJ077s2q79rA" target="_blank" rel="noreferrer">食品饮料行业：站在白酒复苏的前夜 2025-08-26</a><ul><li>通过复盘 2013 年-2015 年这一轮白酒周期，发现白酒股价是提前基本面大概 2 个季度走出底部，也就是股价的底部反转发生在基本面即业绩确认的前 2 个季度。</li></ul></li><li><a href="https://mp.weixin.qq.com/s/9egIrP3WatQZbztRO1XWEA" target="_blank" rel="noreferrer">东兴证券孟斯硕：白酒估值仍处于历史底部 2025-11-25</a><ul><li><ol><li>行业现状与定位</li></ol><ul><li>2025 年三季报显示，A 股白酒上市公司营收与净利润同比双降。</li><li>行业面临“高库存”、“价格倒挂”和“批价疲软”的压力。</li><li>分析师判断，当前已处于行业周期底部。 依据是：宏观经济开始企稳恢复；政策影响最差的时期已过；经销商库存最高点已过去。</li></ul></li><li><ol start="2"><li>驱动逻辑：消费属性（与宏观经济活跃度，如 BCI 产品价格指数、PPI 同步）和金融属性（长期趋势与房地产价格走势一致）</li></ol></li><li><ol start="3"><li>和上轮周期（2013-2014）异同</li></ol><ul><li>相似：均面临宏观经济波动、限制公务消费的政策、以及前期产能扩张导致的供给过剩。</li><li>不同：<br> 本轮消费意愿更弱：家庭资产负债表收缩，健康意识提升。<br> 行业阶段不同：从“增量博弈”进入“存量博弈”，头部企业控盘能力更强，预计本轮周期会更平缓、持续时间更长。</li></ul></li><li><ol start="4"><li>未来趋势与价格带分析</li></ol><ul><li>短期（1-3 年）：消费结构下移，100-200 元价位带因消费基础广泛，成为酒企稳收入的基本盘，穿越周期稳定性更强。</li><li>长期（5 年以上）：仍看好消费升级带动价格上行的趋势，这是行业核心成长动力。</li></ul></li><li><ol start="5"><li>竞争格局与投资方向</li></ol><ul><li>存量市场中，“强者恒强”的马太效应将加剧。</li><li>胜出企业特质：具有全国性品牌影响力、强大渠道掌控力、有创新力的头部酒企。</li><li>消费场景从“悦人”（宴请）向“悦己”（自饮）转变，但长期看两者将并存，与经济繁荣度相关。</li></ul></li><li><ol start="6"><li>关于年轻消费与新兴品类</li></ol><ul><li>年轻人（18-25 岁）并非当前白酒主力，他们更偏好低度、多元化的酒饮（如梅见、气泡酒）。</li><li>酒水消费多元化是趋势，但与其他品类增长并不直接推翻“年轻人不喝白酒”的论调。</li></ul></li><li><ol start="7"><li>投资逻辑与风险提示</li></ol><ul><li>投资逻辑 <ul><li>估值处于历史底部，安全边际较强。</li><li>行业基本面正从底部恢复，稳定分红和业绩预期构成长期投资价值基础。</li><li>长期看好消费升级及龙头企业的确定性增长。</li></ul></li><li>主要风险： <ul><li>宏观复苏与消费的不确定性。</li><li>渠道库存出清速度慢于预期。</li><li>消费价格带变化的风险。</li></ul></li><li>关于年轻化 <blockquote><p>品牌的年轻化一个非常重要的衡量标准是，在年轻人心智中的占领和渗透，如果一个品牌，在年轻人心中有很强的认知，甚至流行，比如说泡泡玛特，那么我们会认为这是一个非常年轻化的品牌。<br> 所以，我们认为那些能够精准定位年轻市场，能够精准定义消费场景，以及在拥有强产品力的同时拥有强文化力的品牌，非常容易成为行业内的黑马。</p></blockquote></li></ul></li></ul></li><li><a href="https://mp.weixin.qq.com/s/2YPXpgf9ycS8162-2sS3eA" target="_blank" rel="noreferrer">周记：聊聊关于白酒的两个争议 2025-08-02 动邪惜独</a><ul><li>感性护城河：白酒属感性消费，用户容忍度高，护城河强于理性健康产品。</li><li>容错性强：即使存在健康争议，消费行为不易受理性负面信息冲击。</li><li>年龄阶段属性：白酒消费随年龄增长而提升，年轻人非目标客群，不影响长期基本盘。</li></ul></li><li><a href="https://www.bilibili.com/video/BV1hQMazjEbP" target="_blank" rel="noreferrer">20250616 白酒 2025 第二期 什么信号指引见底（小瓜投资笔记）</a><ul><li>关注节后酒价，如果节后酒价不跌可能到达供需平衡</li><li>周期行业底部最后一跌，跌穿成本线是常见的情景</li><li>见底信号 <ul><li><ol><li>飞天跌到 1499 零售价，五粮液下调两到三次出厂价；</li></ol></li><li><ol start="2"><li>茅台系列酒收入暴跌，五粮液季度收入连续下跌 6 个季度；</li></ol></li><li><ol start="3"><li>节后酒价不跌，持续一年；</li></ol></li><li><ol start="4"><li>前三项达成，最后一跌加速确认底部。</li></ol></li></ul></li></ul></li><li><a href="https://www.bilibili.com/video/BV1Ca9cYEEJt" target="_blank" rel="noreferrer">白酒 2025 产业周期在变化（20250227 小瓜投资笔记）</a><ul><li>市场逻辑：宏观因素、消费变迁、库存因素</li><li>CR5 和 CR15 的公司，保持 18 年 15%以上增长，幻觉：白酒行业就该永远 15%以上的增长</li><li>危险的线性推演：库存周期后回到 15%增长</li><li>上市公司增长速度持续高于整个白酒行业</li><li>CR5 和 CR15，分别从 14%和 19%上涨到 46%和 55%，9 年来，龙头企业大量蚕食其它中小酒厂的市场份额</li><li>CR15 靠近 80%，增长难度越高，价格战的烈度越强，龙头公司后续复合增长预期下调至 10% 的假设更合理</li><li>现在 10%增长预期；距离规模天花板 50%，4~6 年后触发价格战；2019 年 20%增长预期，距离天花板 150%。风险和机会不同，现在有利条件是分红上升了，利率低了</li><li>2019 15%怎张预期，基础 20PE，分红率 30%，溢价 15%，成长溢价 20%到 27PE；当前 10%怎张，基础 14PE，分红率 50%~70%，溢价 30%，价格战不折价，给到 18.2PE</li><li>增长空间很重要，<strong>成长性的赔率问题</strong>，只有 50%增长空间不可能出 10 倍股，无法基于高估值，产业周期的成长阶段，前期的估值会明显高于后期估值</li><li>关注长期机会，家电行业胜出者美的、格力、海尔，10 年符合收益率在 18%以上，天花板空间被压缩也仍又机会，不能期望 3 年 10 倍的行情</li><li>谨慎分析哪些品牌最能稳定胜出，完成长跑</li><li>（点评：我个人不太认可价格战这个观点，白酒和其它行业不同，基于凡勃伦效应，与其说是价格战，不如说是消费者心智战，降价是毒药，而非武器，降价是为了清库存，以价换量）</li></ul></li></ul><h2 id="同行对比" tabindex="-1">同行对比 <a class="header-anchor" href="#同行对比" aria-label="Permalink to &quot;同行对比&quot;">​</a></h2>',9)),r(a,{codes:["600519","000858","000568","000596","600809"]}),i[1]||(i[1]=l("ol",null,[l("li",null,"对比营业收入及增速"),l("li",null,"对比净利润及增速"),l("li",null,"对比净利率、毛利率、期间费用率"),l("li",null,"对比销售费用、研发费用"),l("li",null,"对比 1 元收入所需 WC")],-1)),r(o,{codes:[{code:"600519",compareItems:{industry:[],product:[],region:[]}},{code:"000858",compareItems:{industry:[],product:[],region:[]}},{code:"000568",compareItems:{industry:[],product:[],region:[]}},{code:"000596",compareItems:{industry:[],product:[],region:[]}},{code:"600809",compareItems:{industry:[],product:[],region:[]}}]}),i[2]||(i[2]=l("ol",null,[l("li",null,"对比主营业务分析")],-1)),r(t,{codes:["600519","000858","000568","000596","600809"]}),i[3]||(i[3]=l("ol",null,[l("li",null,"对比周转率分析"),l("li",null,"对比 1 元收入需要的固定资产分析")],-1)),r(n,{codes:["600519","000858","000568","000596","600809"]}),i[4]||(i[4]=l("ol",null,[l("li",null,"对比 ROE，ROIC 分析")],-1))])}const T=s(d,[["render",m]]);export{P as __pageData,T as default};
