import{_ as r,c as l,o as c,j as e,a as t}from"./chunks/framework.CpuDhtGM.js";const h=JSON.parse('{"title":"电力","description":"","frontmatter":{},"headers":[],"relativePath":"company/electricity/electricity.md","filePath":"company/electricity/electricity.md","lastUpdated":1765520092000}'),i={name:"company/electricity/electricity.md"};function n(o,a,s,d,p,m){return c(),l("div",null,[...a[0]||(a[0]=[e("h1",{id:"电力",tabindex:"-1"},[t("电力 "),e("a",{class:"header-anchor",href:"#电力","aria-label":'Permalink to "电力"'},"​")],-1),e("h2",{id:"水电",tabindex:"-1"},[t("水电 "),e("a",{class:"header-anchor",href:"#水电","aria-label":'Permalink to "水电"'},"​")],-1),e("p",null,"水电行业的产能发挥与下游需求长期比较稳定，成本结构、产量、价格等指标也比较清楚，其本质更像是一个加了杠杆（高负债）的利率产品。",-1),e("p",null,[t("邓晓峰：我以前在公募期间赚钱多的其实是电力股，有一只股票，赚了二三十亿元。当时为什么会觉得这个生意很好？公司下属的一个水电站大约在 2002 年建成，2009 年准备注入上市公司。它是 135 万千瓦，投资 80 多亿元建成，当时我看公司报表突然变成总资产只有 40 亿元，银行贷款还有近 20 亿元，具体数据现在有些忘了。当时看到这个我就觉得很有意思，这个子公司运转这么多年，每年利润的百分之七八十分掉，最后变成总资产不断缩小。我估算了一下，到 2012 年左右时，子公司会把债务全部还完，成为一个没有负债的公司。想想这个生意，最开始 20%的资本金、80%的负债，10 年之后就没有负债了，不考虑分红，仅从投入资本的角度，至少可以赚 4 倍；如果总资产回报率很高，比如到 9%，目标只有 6%，那么可以赚 4×1.5=6 倍。"),e("strong",null,"水电这门生意很简单，只需计算几年把负债还完，本质是项目资源＋杠杆的生意，但是高回报。")],-1),e("p",null,"王世宏：这种生意如果系统性地做，就可以赚很多钱。所有的初期投资一个巨大的固定资产、产能利用率缓慢提升的生意，都有这个特征。前期投了很多钱，产能利用率低的时候涨一点，产能利用率升到 60%～ 70%的时候，有明显的盈利了，股价就开始涨，一直升到 100%时，盈利到顶，股价也涨不动了。这种生意挺多的。",-1),e("p",null,"——《投资中不简单的事》12 深度研究，从市场认知偏差中发掘大机会",-1)])])}const u=r(i,[["render",n]]);export{h as __pageData,u as default};
