import{_ as s,C as l,c as p,o as m,af as c,G as o,j as e}from"./chunks/framework.DH5HlpyC.js";const C=JSON.parse('{"title":"白色家电","description":"","frontmatter":{},"headers":[],"relativePath":"company/home-appliance/home-appliance.md","filePath":"company/home-appliance/home-appliance.md","lastUpdated":1765180995000}'),d={name:"company/home-appliance/home-appliance.md"};function u(h,a,_,b,f,T){const r=l("CompareDataTable"),n=l("ComparePrimaryBusinessTable"),t=l("CompareTurnoverRateTable"),i=l("CompareReturnTable");return m(),p("div",null,[a[0]||(a[0]=c('<h1 id="白色家电" tabindex="-1">白色家电 <a class="header-anchor" href="#白色家电" aria-label="Permalink to &quot;白色家电&quot;">​</a></h1><h2 id="综合信息" tabindex="-1">综合信息 <a class="header-anchor" href="#综合信息" aria-label="Permalink to &quot;综合信息&quot;">​</a></h2><h3 id="文章推荐" tabindex="-1">文章推荐 <a class="header-anchor" href="#文章推荐" aria-label="Permalink to &quot;文章推荐&quot;">​</a></h3><ul><li><a href="https://mp.weixin.qq.com/s/hfwXB2XeIecq5Nd1a38ZLQ" target="_blank" rel="noreferrer">【行业研究】2025 年家电行业研究报告（联合资信）</a></li><li><a href="https://mp.weixin.qq.com/s/Cl1RMyvhGTPb9ia4shbGuw?scene=1&amp;click_id=37" target="_blank" rel="noreferrer">7000 字长文，聊聊家电龙头的投资逻辑（终身黑白）</a></li></ul><h3 id="库存分析" tabindex="-1">库存分析 <a class="header-anchor" href="#库存分析" aria-label="Permalink to &quot;库存分析&quot;">​</a></h3><p>@陈瑾(Jim)：作为家电从业人员，补充几点看法：</p><ol><li>组织架构无好坏，在当下的环境，适合当下的能力最重要，组织形式应该随时市场环境和竞争格局变化而变化；</li><li>海尔跟美的比，优势在于战略清晰，不太会随时摇摆，适合海外这类需要长周期投入类的市场，战略定力是海尔的优势。劣势就是执行力对比美的低（但对于家电行业属于均值之上），价值链的经营效率低于美的，存货周转效率低于美的。美的虽然执行力超强，但问题在于变的过快，所以战略的执行上会有反复摇摆，投入和收缩大开大合，造成不少资源浪费，频繁的调整也容易错失先发优势。</li><li>家电投资的风险提示，家电一般以 3、4 年为一个周期，随着地产周期调整，家电周期理应于 24 年底开始进入调整周期，但因为国补的政策性红利，延缓了周期的到来时间。海尔在上个周期的优势是以卡萨帝代表的高端产品和海外市场，这两点优势边际效用在递减。同时国补期间的高销量（但同时低毛利）拉高各级渠道商的预期，在国补退潮期容易形成大批量存货，其跌价损失对企业利润率的侵蚀巨大，若行业增速触底反弹，高库存有优势（更好的满足需求）与之对比美的的 T+3 和轻库存更适合打逆风战，即使行业持续下滑，较低的存货水平节省下来的资源可以进一步打市场。25 年 8 月以后，家电零售市场已开始转头向下，出现增量不增利的情况。双十一的价格战都不能扭转家电零售渠道下滑的趋势，更加剧了家电渠道间的碾压，预计 26 年的家电市场会更加血雨腥风，到了考验各家耐力的时候。即使再出现政策性极好，家电投资者建议仍需要做好至少 2-3 年行业持续磨底的心里准备和持有周期</li></ol><h2 id="同行对比" tabindex="-1">同行对比 <a class="header-anchor" href="#同行对比" aria-label="Permalink to &quot;同行对比&quot;">​</a></h2>',8)),o(r,{codes:["600690","000651","000333"]}),a[1]||(a[1]=e("ol",null,[e("li",null,"对比营业收入及增速"),e("li",null,"对比净利润及增速"),e("li",null,"对比净利率、毛利率、期间费用率"),e("li",null,"对比销售费用、研发费用"),e("li",null,"对比 1 元收入所需 WC")],-1)),o(n,{codes:[{code:"600690",compareItems:{industry:[],product:["空调"],region:["内","外"]}},{code:"000651",compareItems:{industry:[],product:["空调","消费电器"],region:["内","外"]}},{code:"000333",compareItems:{industry:[],product:["空调","智能家居"],region:["内","外"]}}]}),a[2]||(a[2]=e("ol",null,[e("li",null,"对比主营业务分析")],-1)),o(t,{codes:["600690","000651","000333"]}),a[3]||(a[3]=e("ol",null,[e("li",null,"对比周转率分析"),e("li",null,"对比 1 元收入需要的固定资产分析")],-1)),o(i,{codes:["600690","000651","000333"]}),a[4]||(a[4]=e("ol",null,[e("li",null,"对比 ROE，ROIC 分析")],-1))])}const P=s(d,[["render",u]]);export{C as __pageData,P as default};
