import{_ as q,C as l,c as m,o as _,af as r,G as i,j as e,a as o}from"./chunks/framework.DR80cIop.js";const W=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"company/motorcycle/ninebot.md","filePath":"company/motorcycle/ninebot.md","lastUpdated":1764058112000}'),P={name:"company/motorcycle/ninebot.md"};function T(f,a,x,C,k,R){const t=l("BasicRevenueTable"),n=l("CostsExpensesTable"),h=l("PrimaryBusinessTable"),d=l("BalanceTable"),s=l("WorkingCapitalTable"),u=l("FixedAssetInvestmentAnalysisTable"),b=l("ReturnTable"),p=l("TurnoverRateTable"),c=l("ProfitValuationGroup");return _(),m("div",null,[a[0]||(a[0]=r('<h2 id="九号公司" tabindex="-1">九号公司 <a class="header-anchor" href="#九号公司" aria-label="Permalink to &quot;九号公司&quot;">​</a></h2><h3 id="生意模式" tabindex="-1">生意模式 <a class="header-anchor" href="#生意模式" aria-label="Permalink to &quot;生意模式&quot;">​</a></h3><h3 id="护城河" tabindex="-1">护城河 <a class="header-anchor" href="#护城河" aria-label="Permalink to &quot;护城河&quot;">​</a></h3><h4 id="规模效应和成本控制" tabindex="-1">规模效应和成本控制 <a class="header-anchor" href="#规模效应和成本控制" aria-label="Permalink to &quot;规模效应和成本控制&quot;">​</a></h4><h4 id="技术方面" tabindex="-1">技术方面 <a class="header-anchor" href="#技术方面" aria-label="Permalink to &quot;技术方面&quot;">​</a></h4><h4 id="品牌护城河" tabindex="-1">品牌护城河 <a class="header-anchor" href="#品牌护城河" aria-label="Permalink to &quot;品牌护城河&quot;">​</a></h4><h3 id="行业情况" tabindex="-1">行业情况 <a class="header-anchor" href="#行业情况" aria-label="Permalink to &quot;行业情况&quot;">​</a></h3><h4 id="全球市场" tabindex="-1">全球市场 <a class="header-anchor" href="#全球市场" aria-label="Permalink to &quot;全球市场&quot;">​</a></h4><h4 id="国内市场" tabindex="-1">国内市场 <a class="header-anchor" href="#国内市场" aria-label="Permalink to &quot;国内市场&quot;">​</a></h4><h4 id="新兴市场" tabindex="-1">新兴市场 <a class="header-anchor" href="#新兴市场" aria-label="Permalink to &quot;新兴市场&quot;">​</a></h4><h4 id="市场份额" tabindex="-1">市场份额 <a class="header-anchor" href="#市场份额" aria-label="Permalink to &quot;市场份额&quot;">​</a></h4><h3 id="行业增长空间" tabindex="-1">行业增长空间 <a class="header-anchor" href="#行业增长空间" aria-label="Permalink to &quot;行业增长空间&quot;">​</a></h3><h4 id="趋势" tabindex="-1">趋势 <a class="header-anchor" href="#趋势" aria-label="Permalink to &quot;趋势&quot;">​</a></h4><h4 id="国内市场-1" tabindex="-1">国内市场 <a class="header-anchor" href="#国内市场-1" aria-label="Permalink to &quot;国内市场&quot;">​</a></h4><h4 id="国际市场" tabindex="-1">国际市场 <a class="header-anchor" href="#国际市场" aria-label="Permalink to &quot;国际市场&quot;">​</a></h4><h4 id="新应用场景" tabindex="-1">新应用场景 <a class="header-anchor" href="#新应用场景" aria-label="Permalink to &quot;新应用场景&quot;">​</a></h4><h3 id="行业内" tabindex="-1">行业内 <a class="header-anchor" href="#行业内" aria-label="Permalink to &quot;行业内&quot;">​</a></h3><h4 id="公司在行业内优势" tabindex="-1">公司在行业内优势 <a class="header-anchor" href="#公司在行业内优势" aria-label="Permalink to &quot;公司在行业内优势&quot;">​</a></h4><h4 id="公司的毛利润与行业对比" tabindex="-1">公司的毛利润与行业对比 <a class="header-anchor" href="#公司的毛利润与行业对比" aria-label="Permalink to &quot;公司的毛利润与行业对比&quot;">​</a></h4><h3 id="风险" tabindex="-1">风险 <a class="header-anchor" href="#风险" aria-label="Permalink to &quot;风险&quot;">​</a></h3><h4 id="国际贸易政策变化" tabindex="-1">国际贸易政策变化 <a class="header-anchor" href="#国际贸易政策变化" aria-label="Permalink to &quot;国际贸易政策变化&quot;">​</a></h4><h4 id="国内市场竞争加剧" tabindex="-1">国内市场竞争加剧 <a class="header-anchor" href="#国内市场竞争加剧" aria-label="Permalink to &quot;国内市场竞争加剧&quot;">​</a></h4><h2 id="财务报表分析" tabindex="-1">财务报表分析 <a class="header-anchor" href="#财务报表分析" aria-label="Permalink to &quot;财务报表分析&quot;">​</a></h2><h3 id="一、基本营收数据" tabindex="-1">一、基本营收数据 <a class="header-anchor" href="#一、基本营收数据" aria-label="Permalink to &quot;一、基本营收数据&quot;">​</a></h3>',24)),i(t,{code:"689009"}),a[1]||(a[1]=r('<ol><li>看收入、净利润和利润率</li><li>看收入利润发展过程</li><li>观察盈利质量</li></ol><ul><li>3.1 归母净利润和扣非净利润（是否依靠主营业务，非经常性损益占比）</li><li>3.2 经营利润与金融利润（占比分析，表中经营利润为归母净利润扣除金融利润，。简单粗暴，但颗粒度已经足够）</li><li>3.3 经营净现金流与归母净利润（差异、经营现金流和净利润增长对比）</li><li>3.4 自由现金流（生意能否现金自洽）</li></ul><ol start="4"><li>看机构预测</li></ol><p>我们在进行价值创造方式的分析时，先把货币资金排除在外。</p><h3 id="二、成本费用构成" tabindex="-1">二、成本费用构成 <a class="header-anchor" href="#二、成本费用构成" aria-label="Permalink to &quot;二、成本费用构成&quot;">​</a></h3>',5)),i(n,{code:"689009"}),a[2]||(a[2]=r('<ol><li>研发资本化往往被诟病为操纵利润，需特别注意，研发费用资本化放在开发费用中</li><li>看研发费用总额变化</li><li>看销售费用变化</li><li>销售收入增长是否快于管理费用增长才符合企业运作常识</li><li>管理费用是期间费用，在收入规模扩大的过程中，管理费用比例应当呈下降趋势，这才有规模效应</li></ol><h3 id="三、增长情况" tabindex="-1">三、增长情况 <a class="header-anchor" href="#三、增长情况" aria-label="Permalink to &quot;三、增长情况&quot;">​</a></h3><p>关心增长型企业未来的盈余增长率、它未来能够实现这种增长率的护城河，以及这种增长率对应的不确定性，上述 3 个关心就构成了自由 DCF 和动态估值法的核心要素：未来盈余、永续价值和折现率。</p><p>观察过去 5 年、过去 3 年、未来 3 年的收入增长率、净利润增长率、增速趋势变化</p><h3 id="四、业务构成" tabindex="-1">四、业务构成 <a class="header-anchor" href="#四、业务构成" aria-label="Permalink to &quot;四、业务构成&quot;">​</a></h3><p>每个具体产品的收入、成本和毛利、以及构成收入的明细产品销售数量和价格，以及各项数据的变化趋势。</p><p>更详细的数据需要查阅财报。</p>',7)),i(h,{code:"689009"}),a[3]||(a[3]=e("h3",{id:"五、资产负债",tabindex:"-1"},[o("五、资产负债 "),e("a",{class:"header-anchor",href:"#五、资产负债","aria-label":'Permalink to "五、资产负债"'},"​")],-1)),i(d,{code:"689009"}),a[4]||(a[4]=e("ol",null,[e("li",null,"总资产（轻资产还是重资产，业绩优秀、资产不“重”的企业来说，信息量不多，看一眼就够了。）"),e("li",null,"债务（行业欣欣向荣、利润现金流好的龙头公司无须过度关注。有息债务是多少？现金流是否充沛？）"),e("li",null,"资产负债率"),e("li",null,"经营资产及金融资产（有精力还可以做一个资产负债表的拆分）")],-1)),a[5]||(a[5]=e("h3",{id:"六、投入产出",tabindex:"-1"},[o("六、投入产出 "),e("a",{class:"header-anchor",href:"#六、投入产出","aria-label":'Permalink to "六、投入产出"'},"​")],-1)),a[6]||(a[6]=e("h4",{id:"wc",tabindex:"-1"},[o("WC "),e("a",{class:"header-anchor",href:"#wc","aria-label":'Permalink to "WC"'},"​")],-1)),i(s,{code:"689009"}),a[7]||(a[7]=r('<p>把企业看成一个投入产出系统，目的是服务客户，客户满意，企业获得收入，实现商业循环。</p><ol><li>需要资本性支出购置设备、无形资产和固定设施等，例如制造企业，需要生产设施才能生产产品，其中最重要的是固定资产；</li><li>投入流动资金，例如商业销售，要先垫钱备货；</li><li>投入人力，任何资产都需要人去经营。</li></ol><p>总体而言，要投入 3 项资源：固定资产、WC 和人力。这 3 项投入能帮助我们理解企业商业模式和驱动力，而这 3 项投入隐藏在财务数据后面，我们要进行数据处理才能得到。</p><p>WC（营运资金）=应收账款和票据+预付账款+存货+合同资产-应付账款和票据-预收账款-合同负债</p><p>WC 是投入，产出就是收入，所以衡量 WC 投入产出效率的指标是：1 元收入需要的 WC=WC÷ 销售收入。</p><p>WC 是个期末时间点数字，而销售收入是期间数字。理论上这两个数相除，要把 WC 做期间化处理，一般就是（期初+期末）/2。</p><p>现实中，很多时候我们看趋势变化，所以如果统一都用期末数，影响也不大，如果 WC 期初期末变动剧烈，可以考虑再进一步优化数据，通常用期末数已经足够。另外，应收款项融资需要加回来包括在应收账款和票据中，在 WC 分析场景下，该项目只是应收票据融资后，会计将贴现票据分类处理，性质并没有变化，仍然是企业营运中垫付资金。应付账款和票据，其中票据只包括商业承兑汇票，这是供应商给的信用，而很多情况下银行承兑汇票需预存全额保证金，实际是货币资金置换而不是供应商信用，对于大多数制造类企业来说，商业承兑汇票数量较少，简化可以直接用应付账款而不考虑应付票据的明细情况。</p><p>（新增 WC）是本年 WC-上年 WC，代表本期 WC 的增量，也就是企业在经营中本年需要新投入的资金数，这个属于再投资范围。</p><p>WC 两个用途：</p><ol><li>工具用途。财务分析的尽头是估值，如果用 DCF，需要预测未来自由现金流，而自由现金流等于净利润减去保证净利润产生的再投资，再投资包括两部分：增量 WC 和 CAPEX。现在你就明白我们为什么要算 1 元收入需要的 WC。</li><li>识别商业模式和判断企业竞争地位。正常情况下，企业需要垫付流动资金，而垫付多少取决于生意是怎么做的，以及该企业与上下游企业之间的竞争地位。我们需要把它放在背景中去对比（一种是对比不同行业、不同商业模式下指标的变化，另一种是与同行业公司对比）​。</li></ol><ul><li><strong>WC &gt; 0 （正数）</strong>： 意味着你垫付出去的钱，多于你占用的钱。你的生意需要<strong>额外的运营资金</strong>来周转。这通常会占用公司的现金，对现金流不利。</li><li><strong>WC &lt; 0 （负数）</strong>： 意味着你占用的上下游的钱，多于你垫付出去的钱。你的生意不仅不需要自己掏钱周转，反而**能把别人的钱当成“无息贷款”**来用。这对现金流极为有利。</li></ul><p>WC 是实际经营性流动资产减去经营性流动负债，衡量净流动资产的投入产出效率，表示企业为了服务客户取得收入在净流动资产方面的投入。</p><h4 id="固定资产" tabindex="-1">固定资产 <a class="header-anchor" href="#固定资产" aria-label="Permalink to &quot;固定资产&quot;">​</a></h4>',13)),i(u,{code:"689009"}),a[8]||(a[8]=r('<p>这里的数据还是用期末数，如果特别重要，也就是资产特别“重”​，数字变动剧烈的，考虑采用期间数 ​（期初+期末）/2</p><p>固定资产=资产负债表上的固定资产+在建工程+工程物资-固定资产清理（理由是，固定资产的其他项目，特别是在建工程，在会计处理上容易被操纵。有时看到表上有一堆在建工程尚未转固定资产，何时转、转多少都有操纵空间，所以放到一起看，一来避免被误导，二来这些前期投入已经进入为企业经营准备的固定资产中，应该一起来看。）</p><p>长期资产=固定资产+资产负债表其他长期经营资产（无形资产+开发费+使用权资产+商誉+长期待摊）</p><p>折旧分析：研发开支全部费用化了、资本开支并不大，一年差不多 30-40 亿元左右，如果考虑到每年的 15 亿元的折旧，资本开支每年仅仅会侵蚀 15-25 亿元的净利润现金流，也就是说格力电器绝大部分净利润都是自由现金流，可以自由支配。</p><p>如果企业的固定资产和无形资产与企业的营业收入长期处于关联度较低的情况，相应的资产减值损失很快就会发生。经营资产如果不能推动营业收入持续增加，就会逐渐沦为不良资产，哪怕它的物理质量非常好。</p><h4 id="人员投入" tabindex="-1">人员投入 <a class="header-anchor" href="#人员投入" aria-label="Permalink to &quot;人员投入&quot;">​</a></h4><p>商业模式从驱动力视角来看，大体可以分为产品驱动、人力驱动和资本驱动。</p><p>资本驱动是金融企业。人力驱动主要是产品服务定制化的商业模式，缺点是规模效应差，要增加收入就要多投入人力。</p><p>人员投入的数据需要去财报中找，主要观察下面的数据的变化趋势</p><ul><li>年份</li><li>人数</li><li>生产人员</li><li>销售人员</li><li>研发人员</li><li>生产人员占比</li><li>销售人员占比</li><li>研发人员占比</li><li>人均收入</li><li>人均归母净利润</li><li>人均扣非净利润</li><li>人均薪酬</li><li>人均固定资产</li></ul><p>还要和其它同行业企业进行横向对比，造成这些差异的原因是什么。</p><h3 id="七、收益率" tabindex="-1">七、收益率 <a class="header-anchor" href="#七、收益率" aria-label="Permalink to &quot;七、收益率&quot;">​</a></h3><p>收益率指标一般看这么几个：ROE、资产回报率(Return on Assets, ROA)和资本投入回报率(Return on Investment Capital, ROIC)，以及前面我们已经算过的净经营资产收益率。收益率当然是越高越好，持续高更好，我的大体感觉，ROE 在 20%以上的都是比较优秀的公司。ROA 主要和 ROE 结合起来看，看在去掉杠杆效应的情况下总资产的获利能力。</p><p>通常用杜邦分析法分析 ROE 波动背后的原因</p><p>ROE、ROA 我们常用，ROIC 用得少一点，主要是 ROIC 数据需要处理，分解不那么容易，所以要去理解 ROIC 背后的驱动原因有些困难[ROIC=税后净运营利润 NOPAT(NetOperating Profit After Tax)/投资资本，NOPAT=（营业利润+利息支出）×（1-所得税率）​，投资资本=总资产-无息流动负债-商誉]​。ROIC 在评估盈利能力时同时考虑了股权资本和债权资本的影响，可以更加客观地评估公司在无杠杆下的盈利能力。</p><p>ROIC 只要与 ROE 同步就问题不大，由于 ROIC 的拆解比较复杂，非专业投资人看看指标就好，如果偏离很大，就需要去找找原因。科沃斯这里差不多，我们就不浪费时间了。</p>',16)),i(b,{code:"689009"}),a[9]||(a[9]=e("p",null,"如果有精力，可对资产周转率进一步拆解，资产周转率代表资产效率，拆解后可以对资产效率变化的原因有进一步了解。",-1)),a[10]||(a[10]=e("p",null,"想拆解资产周转率指标，首先准备平均资产数据。平均资产数=（期初数+期末数）/2，周转天数=365× 资产/收入或者主营业务成本，存货对应主营业务成本，其他对应销售收入。",-1)),i(p,{code:"689009"}),a[11]||(a[11]=r('<h2 id="非财务信息-八问" tabindex="-1">非财务信息“八问” <a class="header-anchor" href="#非财务信息-八问" aria-label="Permalink to &quot;非财务信息“八问”&quot;">​</a></h2><h3 id="靠什么赚钱" tabindex="-1">靠什么赚钱？ <a class="header-anchor" href="#靠什么赚钱" aria-label="Permalink to &quot;靠什么赚钱？&quot;">​</a></h3><p>搞清楚生意是怎么做的，核心驱动力是什么</p><h3 id="顺风还是逆风" tabindex="-1">顺风还是逆风？ <a class="header-anchor" href="#顺风还是逆风" aria-label="Permalink to &quot;顺风还是逆风？&quot;">​</a></h3><p>外部大环境境好不好</p><h3 id="空间有多大" tabindex="-1">空间有多大？ <a class="header-anchor" href="#空间有多大" aria-label="Permalink to &quot;空间有多大？&quot;">​</a></h3><p>行业未来空间有多大，未来发展的天花板</p><h3 id="竞争格局好不好" tabindex="-1">竞争格局好不好？ <a class="header-anchor" href="#竞争格局好不好" aria-label="Permalink to &quot;竞争格局好不好？&quot;">​</a></h3><p>认识行业竞争的玩法</p><h3 id="有什么优势" tabindex="-1">有什么优势？ <a class="header-anchor" href="#有什么优势" aria-label="Permalink to &quot;有什么优势？&quot;">​</a></h3><p>与同行相比，有什么优势</p><h3 id="管理层行不行" tabindex="-1">管理层行不行？ <a class="header-anchor" href="#管理层行不行" aria-label="Permalink to &quot;管理层行不行？&quot;">​</a></h3><p>特别关注老板和管理层，公司治理有没有问题</p><h3 id="风险在哪里" tabindex="-1">风险在哪里？ <a class="header-anchor" href="#风险在哪里" aria-label="Permalink to &quot;风险在哪里？&quot;">​</a></h3><p>不利因素有哪些，风险可能出现在什么地方，没达到预期的薄弱环节在哪里</p><h3 id="未来会怎样" tabindex="-1">未来会怎样？ <a class="header-anchor" href="#未来会怎样" aria-label="Permalink to &quot;未来会怎样？&quot;">​</a></h3><p>未来会怎样，对未来做出预测</p><h2 id="估值和结论" tabindex="-1">估值和结论 <a class="header-anchor" href="#估值和结论" aria-label="Permalink to &quot;估值和结论&quot;">​</a></h2>',18)),i(c,{code:"689009"}),a[12]||(a[12]=e("h2",{id:"零散思考",tabindex:"-1"},[o("零散思考 "),e("a",{class:"header-anchor",href:"#零散思考","aria-label":'Permalink to "零散思考"'},"​")],-1)),a[13]||(a[13]=e("p",null,"格力主要的客户群体以中年人为主，他们对价格相对不敏感，更看重产品的品质和耐用性。而小米的受众更多是年轻人，尤其是租房群体，对价格比较敏感，追求“够用就好”，不倾向于购买过高溢价的产品。美的则处于两者之间，覆盖了中间价格带和多元需求。因此在国补这类以价格拉动为导向的政策刺激下，小米和美的反而更容易吸引对补贴敏感的人群，销量表现自然会更突出。",-1))])}const I=q(P,[["render",T]]);export{W as __pageData,I as default};
