import{_ as i,c as a,o as t,af as r}from"./chunks/framework.CpuDhtGM.js";const g=JSON.parse('{"title":"资本周期","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/cycles/capital.md","filePath":"value-investing/cycles/capital.md","lastUpdated":1764992065000}'),e={name:"value-investing/cycles/capital.md"};function o(n,l,s,h,u,d){return t(),a("div",null,[...l[0]||(l[0]=[r('<h1 id="资本周期" tabindex="-1">资本周期 <a class="header-anchor" href="#资本周期" aria-label="Permalink to &quot;资本周期&quot;">​</a></h1><p>资本周期是指资本在行业间流入流出的周期性变化，是驱动行业景气和盈利波动的重要因素。</p><h2 id="资本周期原理" tabindex="-1">资本周期原理 <a class="header-anchor" href="#资本周期原理" aria-label="Permalink to &quot;资本周期原理&quot;">​</a></h2><p>资本周期反映了资本对行业回报率的追逐行为：</p><ul><li><strong>高盈利吸引资本流入</strong>：当某行业盈利能力较强时，会吸引新资本进入</li><li><strong>资本增加导致竞争加剧</strong>：新进入者和产能扩张使竞争加剧</li><li><strong>竞争压低盈利水平</strong>：供给增加导致价格下降，盈利能力减弱</li><li><strong>低盈利促使资本退出</strong>：盈利能力下降导致资本流出</li><li><strong>资本减少供需改善</strong>：产能退出或整合使供需关系改善</li><li><strong>供需改善推高盈利</strong>：供给减少使议价能力提升，盈利恢复</li></ul><h2 id="资本周期阶段" tabindex="-1">资本周期阶段 <a class="header-anchor" href="#资本周期阶段" aria-label="Permalink to &quot;资本周期阶段&quot;">​</a></h2><h3 id="资本流入期" tabindex="-1">资本流入期 <a class="header-anchor" href="#资本流入期" aria-label="Permalink to &quot;资本流入期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>行业盈利水平较高</li><li>新进入者增多</li><li>现有企业扩产</li><li>投资增加产能过剩</li></ul><p><strong>投资策略：</strong></p><ul><li>谨慎对待，避免追高</li><li>关注成本优势明显的龙头企业</li><li>准备迎接盈利下滑</li></ul><h3 id="竞争加剧期" tabindex="-1">竞争加剧期 <a class="header-anchor" href="#竞争加剧期" aria-label="Permalink to &quot;竞争加剧期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>供给大幅增加</li><li>价格战开始</li><li>盈利能力下降</li><li>行业整合加速</li></ul><p><strong>投资策略：</strong></p><ul><li>规避大部分企业</li><li>关注财务稳健的龙头</li><li>寻找并购整合机会</li></ul><h3 id="资本退出期" tabindex="-1">资本退出期 <a class="header-anchor" href="#资本退出期" aria-label="Permalink to &quot;资本退出期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>亏损企业退出</li><li>产能开始收缩</li><li>供需关系改善</li><li>价格企稳回升</li></ul><p><strong>投资策略：</strong></p><ul><li>开始关注行业机会</li><li>选择存活下来的优质企业</li><li>耐心等待盈利改善</li></ul><h3 id="供给短缺期" tabindex="-1">供给短缺期 <a class="header-anchor" href="#供给短缺期" aria-label="Permalink to &quot;供给短缺期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>产能明显不足</li><li>价格大幅上涨</li><li>盈利能力恢复</li><li>资本开始重新关注</li></ul><p><strong>投资策略：</strong></p><ul><li>重点配置优质标的</li><li>享受价格上涨和盈利改善</li><li>关注新资本流入的时点</li></ul><h2 id="不同行业资本周期特点" tabindex="-1">不同行业资本周期特点 <a class="header-anchor" href="#不同行业资本周期特点" aria-label="Permalink to &quot;不同行业资本周期特点&quot;">​</a></h2><h3 id="资本密集型行业" tabindex="-1">资本密集型行业 <a class="header-anchor" href="#资本密集型行业" aria-label="Permalink to &quot;资本密集型行业&quot;">​</a></h3><ul><li><strong>特点</strong>：投资规模大，周期长</li><li><strong>代表</strong>：钢铁、化工、航空</li><li><strong>投资要点</strong>：关注产能投放节奏和需求变化</li></ul><h3 id="技术密集型行业" tabindex="-1">技术密集型行业 <a class="header-anchor" href="#技术密集型行业" aria-label="Permalink to &quot;技术密集型行业&quot;">​</a></h3><ul><li><strong>特点</strong>：技术迭代快，竞争格局变化快</li><li><strong>代表</strong>：半导体、新能源</li><li><strong>投资要点</strong>：关注技术路线和领先企业</li></ul><h3 id="消费型行业" tabindex="-1">消费型行业 <a class="header-anchor" href="#消费型行业" aria-label="Permalink to &quot;消费型行业&quot;">​</a></h3><ul><li><strong>特点</strong>：品牌和渠道更重要</li><li><strong>代表</strong>：食品饮料、家电</li><li><strong>投资要点</strong>：关注品牌壁垒和渠道优势</li></ul><h2 id="投资应用" tabindex="-1">投资应用 <a class="header-anchor" href="#投资应用" aria-label="Permalink to &quot;投资应用&quot;">​</a></h2><h3 id="如何识别资本周期" tabindex="-1">如何识别资本周期 <a class="header-anchor" href="#如何识别资本周期" aria-label="Permalink to &quot;如何识别资本周期&quot;">​</a></h3><ul><li>监控行业资本开支数据</li><li>关注新进入者和产能规划</li><li>分析行业产能利用率</li><li>跟踪企业盈利能力变化</li></ul><h3 id="资本周期投资策略" tabindex="-1">资本周期投资策略 <a class="header-anchor" href="#资本周期投资策略" aria-label="Permalink to &quot;资本周期投资策略&quot;">​</a></h3><ul><li>逆周期投资，在资本退出期布局</li><li>选择具有成本优势的龙头企业</li><li>耐心等待周期转换</li><li>分散投资降低行业集中度风险</li></ul>',38)])])}const p=i(e,[["render",o]]);export{g as __pageData,p as default};
