import{_ as i,c as a,o as t,af as e}from"./chunks/framework.DH5HlpyC.js";const g=JSON.parse('{"title":"经济周期","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/cycles/economic.md","filePath":"value-investing/cycles/economic.md","lastUpdated":1764992065000}'),r={name:"value-investing/cycles/economic.md"};function o(n,l,s,h,u,c){return t(),a("div",null,[...l[0]||(l[0]=[e('<h1 id="经济周期" tabindex="-1">经济周期 <a class="header-anchor" href="#经济周期" aria-label="Permalink to &quot;经济周期&quot;">​</a></h1><p>经济周期是市场经济活动的周期性波动，对各类资产价格和投资回报产生重要影响。</p><h2 id="经济周期阶段" tabindex="-1">经济周期阶段 <a class="header-anchor" href="#经济周期阶段" aria-label="Permalink to &quot;经济周期阶段&quot;">​</a></h2><h3 id="扩张期" tabindex="-1">扩张期 <a class="header-anchor" href="#扩张期" aria-label="Permalink to &quot;扩张期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>GDP增长加速</li><li>失业率下降</li><li>通胀温和上升</li><li>企业盈利改善</li></ul><p><strong>投资机会：</strong></p><ul><li>股票市场表现良好</li><li>周期性股票领涨</li><li>可考虑适当加杠杆</li></ul><h3 id="高峰期" tabindex="-1">高峰期 <a class="header-anchor" href="#高峰期" aria-label="Permalink to &quot;高峰期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>经济增长见顶</li><li>通胀压力加大</li><li>货币政策收紧</li><li>市场情绪乐观</li></ul><p><strong>投资策略：</strong></p><ul><li>逐步降低风险敞口</li><li>增持防御性资产</li><li>关注估值风险</li></ul><h3 id="衰退期" tabindex="-1">衰退期 <a class="header-anchor" href="#衰退期" aria-label="Permalink to &quot;衰退期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>经济负增长</li><li>失业率上升</li><li>通缩压力</li><li>企业盈利下滑</li></ul><p><strong>投资机会：</strong></p><ul><li>债券表现相对较好</li><li>防御性股票相对抗跌</li><li>等待优质资产价格回归合理</li></ul><h3 id="复苏期" tabindex="-1">复苏期 <a class="header-anchor" href="#复苏期" aria-label="Permalink to &quot;复苏期&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>经济开始恢复</li><li>政策宽松支持</li><li>企业信心回升</li><li>市场预期改善</li></ul><p><strong>投资策略：</strong></p><ul><li>提前布局受益行业</li><li>增加股票配置</li><li>关注流动性改善机会</li></ul><h2 id="经济指标监控" tabindex="-1">经济指标监控 <a class="header-anchor" href="#经济指标监控" aria-label="Permalink to &quot;经济指标监控&quot;">​</a></h2><h3 id="领先指标" tabindex="-1">领先指标 <a class="header-anchor" href="#领先指标" aria-label="Permalink to &quot;领先指标&quot;">​</a></h3><ul><li>制造业PMI</li><li>新增信贷</li><li>股票指数</li><li>消费者信心指数</li></ul><h3 id="同步指标" tabindex="-1">同步指标 <a class="header-anchor" href="#同步指标" aria-label="Permalink to &quot;同步指标&quot;">​</a></h3><ul><li>GDP增长率</li><li>工业增加值</li><li>就业数据</li><li>零售销售</li></ul><h3 id="滞后指标" tabindex="-1">滞后指标 <a class="header-anchor" href="#滞后指标" aria-label="Permalink to &quot;滞后指标&quot;">​</a></h3><ul><li>CPI通胀率</li><li>失业率</li><li>利率水平</li><li>企业存货</li></ul><h2 id="投资配置策略" tabindex="-1">投资配置策略 <a class="header-anchor" href="#投资配置策略" aria-label="Permalink to &quot;投资配置策略&quot;">​</a></h2><h3 id="根据周期调整资产配置" tabindex="-1">根据周期调整资产配置 <a class="header-anchor" href="#根据周期调整资产配置" aria-label="Permalink to &quot;根据周期调整资产配置&quot;">​</a></h3><ul><li><strong>扩张期</strong>：股票&gt;商品&gt;债券&gt;现金</li><li><strong>高峰期</strong>：现金&gt;债券&gt;股票&gt;商品</li><li><strong>衰退期</strong>：债券&gt;现金&gt;股票&gt;商品</li><li><strong>复苏期</strong>：股票&gt;债券&gt;商品&gt;现金</li></ul><h3 id="行业轮动策略" tabindex="-1">行业轮动策略 <a class="header-anchor" href="#行业轮动策略" aria-label="Permalink to &quot;行业轮动策略&quot;">​</a></h3><ul><li><strong>扩张期</strong>：科技、金融、可选消费</li><li><strong>高峰期</strong>：能源、原材料、工业</li><li><strong>衰退期</strong>：公用事业、医疗保健、必需消费</li><li><strong>复苏期</strong>：金融、房地产、可选消费</li></ul>',35)])])}const p=i(r,[["render",o]]);export{g as __pageData,p as default};
