import{_ as i,c as a,o as r,af as t}from"./chunks/framework.DR80cIop.js";const c=JSON.parse('{"title":"库存周期","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/cycles/inventory.md","filePath":"value-investing/cycles/inventory.md","lastUpdated":1764992065000}'),e={name:"value-investing/cycles/inventory.md"};function o(n,l,s,h,u,d){return r(),a("div",null,[...l[0]||(l[0]=[t('<h1 id="库存周期" tabindex="-1">库存周期 <a class="header-anchor" href="#库存周期" aria-label="Permalink to &quot;库存周期&quot;">​</a></h1><p>库存周期是经济周期的重要组成部分，反映了企业库存水平的周期性变化对经济活动的影响。</p><h2 id="库存周期理论" tabindex="-1">库存周期理论 <a class="header-anchor" href="#库存周期理论" aria-label="Permalink to &quot;库存周期理论&quot;">​</a></h2><p>库存周期通常持续3-4年，包括四个阶段：</p><ul><li><strong>主动补库存</strong>：企业预期向好，主动增加库存</li><li><strong>被动补库存</strong>：需求下滑，库存被动积压</li><li><strong>主动去库存</strong>：企业预期悲观，主动减少库存</li><li><strong>被动去库存</strong>：需求回升，库存被动消化</li></ul><h2 id="四个阶段分析" tabindex="-1">四个阶段分析 <a class="header-anchor" href="#四个阶段分析" aria-label="Permalink to &quot;四个阶段分析&quot;">​</a></h2><h3 id="主动补库存-繁荣期" tabindex="-1">主动补库存（繁荣期） <a class="header-anchor" href="#主动补库存-繁荣期" aria-label="Permalink to &quot;主动补库存（繁荣期）&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>需求旺盛，销售增长</li><li>企业对未来乐观</li><li>主动增加原材料和产成品库存</li><li>产能利用率提高</li></ul><p><strong>市场表现：</strong></p><ul><li>原材料价格上涨</li><li>工业产出增加</li><li>企业收入利润双增长</li></ul><p><strong>投资机会：</strong></p><ul><li>上游资源品股票</li><li>中游制造业股票</li><li>关注需求持续性和通胀压力</li></ul><h3 id="被动补库存-衰退初期" tabindex="-1">被动补库存（衰退初期） <a class="header-anchor" href="#被动补库存-衰退初期" aria-label="Permalink to &quot;被动补库存（衰退初期）&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>需求开始下滑</li><li>库存被动积压</li><li>产能利用率下降</li><li>企业开始调整生产计划</li></ul><p><strong>市场表现：</strong></p><ul><li>原材料价格见顶回落</li><li>库存数据居高不下</li><li>企业盈利增速放缓</li></ul><p><strong>投资策略：</strong></p><ul><li>减持周期股</li><li>关注防御性行业</li><li>规避库存高企的企业</li></ul><h3 id="主动去库存-衰退期" tabindex="-1">主动去库存（衰退期） <a class="header-anchor" href="#主动去库存-衰退期" aria-label="Permalink to &quot;主动去库存（衰退期）&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>需求疲软，销售下滑</li><li>企业主动削减库存</li><li>减少生产，裁员</li><li>价格竞争加剧</li></ul><p><strong>市场表现：</strong></p><ul><li>原材料价格大幅下跌</li><li>工业产出收缩</li><li>通货紧缩压力</li></ul><p><strong>投资机会：</strong></p><ul><li>防御性行业</li><li>现金类资产</li><li>等待底部信号</li></ul><h3 id="被动去库存-复苏期" tabindex="-1">被动去库存（复苏期） <a class="header-anchor" href="#被动去库存-复苏期" aria-label="Permalink to &quot;被动去库存（复苏期）&quot;">​</a></h3><p><strong>特征：</strong></p><ul><li>需求开始回升</li><li>库存快速消化</li><li>产能利用率提高</li><li>企业重新开始生产</li></ul><p><strong>市场表现：</strong></p><ul><li>原材料价格企稳回升</li><li>工业产出恢复增长</li><li>企业盈利改善</li></ul><p><strong>投资策略：</strong></p><ul><li>提前布局周期股</li><li>关注库存数据变化</li><li>把握经济复苏机会</li></ul><h2 id="库存指标监控" tabindex="-1">库存指标监控 <a class="header-anchor" href="#库存指标监控" aria-label="Permalink to &quot;库存指标监控&quot;">​</a></h2><h3 id="主要指标" tabindex="-1">主要指标 <a class="header-anchor" href="#主要指标" aria-label="Permalink to &quot;主要指标&quot;">​</a></h3><ul><li><strong>工业企业产成品库存</strong>：反映库存水平变化</li><li><strong>PMI库存指数</strong>：采购经理人调查的库存数据</li><li><strong>存货周转天数</strong>：企业库存管理效率</li><li><strong>原材料库存指数</strong>：上游库存变化</li></ul><h3 id="分析要点" tabindex="-1">分析要点 <a class="header-anchor" href="#分析要点" aria-label="Permalink to &quot;分析要点&quot;">​</a></h3><ul><li>关注库存同比变化趋势</li><li>结合需求和产能分析</li><li>注意库存结构（原材料vs产成品）</li><li>考虑不同行业库存周期差异</li></ul><h2 id="行业差异" tabindex="-1">行业差异 <a class="header-anchor" href="#行业差异" aria-label="Permalink to &quot;行业差异&quot;">​</a></h2><h3 id="上游资源行业" tabindex="-1">上游资源行业 <a class="header-anchor" href="#上游资源行业" aria-label="Permalink to &quot;上游资源行业&quot;">​</a></h3><ul><li><strong>特点</strong>：库存周期波动大</li><li><strong>投资要点</strong>：关注价格趋势和供需关系</li></ul><h3 id="中游制造业" tabindex="-1">中游制造业 <a class="header-anchor" href="#中游制造业" aria-label="Permalink to &quot;中游制造业&quot;">​</a></h3><ul><li><strong>特点</strong>：库存管理更精细</li><li><strong>投资要点</strong>：关注产能利用率和库存周转</li></ul><h3 id="下游消费行业" tabindex="-1">下游消费行业 <a class="header-anchor" href="#下游消费行业" aria-label="Permalink to &quot;下游消费行业&quot;">​</a></h3><ul><li><strong>特点</strong>：库存相对稳定</li><li><strong>投资要点</strong>：关注渠道库存和终端销售</li></ul><h2 id="投资应用" tabindex="-1">投资应用 <a class="header-anchor" href="#投资应用" aria-label="Permalink to &quot;投资应用&quot;">​</a></h2><h3 id="如何利用库存周期" tabindex="-1">如何利用库存周期 <a class="header-anchor" href="#如何利用库存周期" aria-label="Permalink to &quot;如何利用库存周期&quot;">​</a></h3><ul><li>监控库存数据判断经济周期位置</li><li>在被动去库存期提前布局</li><li>在主动补库存期享受上涨行情</li><li>在被动补库存期及时减仓</li></ul><h3 id="风险控制" tabindex="-1">风险控制 <a class="header-anchor" href="#风险控制" aria-label="Permalink to &quot;风险控制&quot;">​</a></h3><ul><li>库存周期存在不确定性</li><li>政策干预可能改变周期节奏</li><li>需要结合其他指标综合判断</li><li>注意不同行业的周期性差异</li></ul>',51)])])}const p=i(e,[["render",o]]);export{c as __pageData,p as default};
