import{_ as a,c as i,o as e,af as t}from"./chunks/framework.DH5HlpyC.js";const m=JSON.parse('{"title":"周期股投资","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/investment-style/cyclical.md","filePath":"value-investing/investment-style/cyclical.md","lastUpdated":1764992065000}'),r={name:"value-investing/investment-style/cyclical.md"};function n(o,l,s,h,c,d){return e(),i("div",null,[...l[0]||(l[0]=[t('<h1 id="周期股投资" tabindex="-1">周期股投资 <a class="header-anchor" href="#周期股投资" aria-label="Permalink to &quot;周期股投资&quot;">​</a></h1><p>周期股投资是针对与经济周期高度相关的行业公司进行投资，通过把握周期变化获得收益。</p><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>周期股是指业绩和股价随经济周期波动明显的股票，主要分布在：</p><ul><li>原材料行业</li><li>工业制造</li><li>金融服务</li><li>房地产</li><li>消费品（非必需）</li></ul><h2 id="周期特征" tabindex="-1">周期特征 <a class="header-anchor" href="#周期特征" aria-label="Permalink to &quot;周期特征&quot;">​</a></h2><ul><li><strong>业绩波动大</strong>：盈利随经济周期起伏</li><li><strong>股价弹性高</strong>：牛市涨得多，熊市跌得凶</li><li><strong>时机敏感</strong>：买卖时机要求高</li><li><strong>杠杆效应</strong>：业绩和股价都有放大效应</li></ul><h2 id="周期阶段投资策略" tabindex="-1">周期阶段投资策略 <a class="header-anchor" href="#周期阶段投资策略" aria-label="Permalink to &quot;周期阶段投资策略&quot;">​</a></h2><h3 id="萧条期-投资最佳时机" tabindex="-1">萧条期（投资最佳时机） <a class="header-anchor" href="#萧条期-投资最佳时机" aria-label="Permalink to &quot;萧条期（投资最佳时机）&quot;">​</a></h3><ul><li>特征：市场悲观，股价低廉</li><li>策略：逢低布局优质标的</li><li>关注：现金流充足、负债率低的企业</li></ul><h3 id="复苏期" tabindex="-1">复苏期 <a class="header-anchor" href="#复苏期" aria-label="Permalink to &quot;复苏期&quot;">​</a></h3><ul><li>特征：需求回升，业绩改善</li><li>策略：加仓周期股，享受上涨行情</li><li>关注：杠杆高的弹性标的</li></ul><h3 id="繁荣期-逐步退出" tabindex="-1">繁荣期（逐步退出） <a class="header-anchor" href="#繁荣期-逐步退出" aria-label="Permalink to &quot;繁荣期（逐步退出）&quot;">​</a></h3><ul><li>特征：市场乐观，股价高企</li><li>策略：分批减仓，锁定收益</li><li>关注：行业景气度见顶信号</li></ul><h3 id="衰退期-等待时机" tabindex="-1">衰退期（等待时机） <a class="header-anchor" href="#衰退期-等待时机" aria-label="Permalink to &quot;衰退期（等待时机）&quot;">​</a></h3><ul><li>特征：需求下滑，业绩恶化</li><li>策略：观望为主，等待机会</li><li>关注：行业底部信号</li></ul><h2 id="风险控制" tabindex="-1">风险控制 <a class="header-anchor" href="#风险控制" aria-label="Permalink to &quot;风险控制&quot;">​</a></h2><ul><li>严格把握买卖时机</li><li>分散投资降低单一行业风险</li><li>设置止损位控制下行风险</li><li>关注宏观经济指标变化</li></ul>',18)])])}const _=a(r,[["render",n]]);export{m as __pageData,_ as default};
