import{_ as l,c as i,o as e,af as t}from"./chunks/framework.CpuDhtGM.js";const _=JSON.parse('{"title":"价值股投资","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/investment-style/value.md","filePath":"value-investing/investment-style/value.md","lastUpdated":1764992065000}'),r={name:"value-investing/investment-style/value.md"};function n(o,a,s,h,u,d){return e(),i("div",null,[...a[0]||(a[0]=[t('<h1 id="价值股投资" tabindex="-1">价值股投资 <a class="header-anchor" href="#价值股投资" aria-label="Permalink to &quot;价值股投资&quot;">​</a></h1><p>价值股投资是通过分析公司内在价值，寻找被市场低估的股票进行投资，等待价格回归价值。</p><h2 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h2><p>价值股是指当前市场价格低于其内在价值的股票，通常具有以下特征：</p><ul><li>低市盈率、低市净率</li><li>稳定的盈利能力</li><li>持续的分红政策</li><li>较低的市场关注度</li></ul><h2 id="价值投资原理" tabindex="-1">价值投资原理 <a class="header-anchor" href="#价值投资原理" aria-label="Permalink to &quot;价值投资原理&quot;">​</a></h2><ul><li><strong>市场有效假说的例外</strong>：市场并非完全有效，存在定价错误</li><li><strong>均值回归</strong>：价格最终会回归内在价值</li><li><strong>安全边际</strong>：以低于内在价值的价格买入获得保护</li></ul><h2 id="价值评估方法" tabindex="-1">价值评估方法 <a class="header-anchor" href="#价值评估方法" aria-label="Permalink to &quot;价值评估方法&quot;">​</a></h2><h3 id="相对估值法" tabindex="-1">相对估值法 <a class="header-anchor" href="#相对估值法" aria-label="Permalink to &quot;相对估值法&quot;">​</a></h3><ul><li>市盈率（P/E）对比</li><li>市净率（P/B）对比</li><li>市销率（P/S）对比</li><li>EV/EBITDA对比</li></ul><h3 id="绝对估值法" tabindex="-1">绝对估值法 <a class="header-anchor" href="#绝对估值法" aria-label="Permalink to &quot;绝对估值法&quot;">​</a></h3><ul><li>现金流折现模型（DCF）</li><li>股息折现模型（DDM）</li><li>资产基础法</li></ul><h2 id="投资策略" tabindex="-1">投资策略 <a class="header-anchor" href="#投资策略" aria-label="Permalink to &quot;投资策略&quot;">​</a></h2><ol><li><strong>价值识别</strong>：建立价值评估体系</li><li><strong>耐心等待</strong>：等待合适的买入时机</li><li><strong>分散投资</strong>：降低个股风险</li><li><strong>长期持有</strong>：给价值回归时间</li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><ul><li>避免价值陷阱（低估值有低估值的原因）</li><li>关注基本面变化趋势</li><li>结合宏观环境判断</li><li>保持理性和耐心</li></ul>',16)])])}const m=l(r,[["render",n]]);export{_ as __pageData,m as default};
