import{_ as a,c as i,o as e,af as t}from"./chunks/framework.DR80cIop.js";const c=JSON.parse('{"title":"相对估值","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/valuation/relative.md","filePath":"value-investing/valuation/relative.md","lastUpdated":1764992065000}'),s={name:"value-investing/valuation/relative.md"};function o(n,l,r,h,u,p){return e(),i("div",null,[...l[0]||(l[0]=[t('<h1 id="相对估值" tabindex="-1">相对估值 <a class="header-anchor" href="#相对估值" aria-label="Permalink to &quot;相对估值&quot;">​</a></h1><p>相对估值是通过与可比公司或历史估值水平进行比较来确定目标公司价值的方法。</p><h2 id="相对估值原理" tabindex="-1">相对估值原理 <a class="header-anchor" href="#相对估值原理" aria-label="Permalink to &quot;相对估值原理&quot;">​</a></h2><h3 id="基本概念" tabindex="-1">基本概念 <a class="header-anchor" href="#基本概念" aria-label="Permalink to &quot;基本概念&quot;">​</a></h3><p>相对估值的核心思想是&quot;相似公司应该有相似的估值&quot;，通过市场定价的相对比较来判断投资价值。</p><h3 id="理论基础" tabindex="-1">理论基础 <a class="header-anchor" href="#理论基础" aria-label="Permalink to &quot;理论基础&quot;">​</a></h3><ul><li><strong>有效市场假说</strong>：市场总体上是有效的</li><li><strong>替代原则</strong>：相似资产可以相互替代</li><li><strong>相对价值</strong>：价值是相对比较的结果</li></ul><h2 id="常用相对估值指标" tabindex="-1">常用相对估值指标 <a class="header-anchor" href="#常用相对估值指标" aria-label="Permalink to &quot;常用相对估值指标&quot;">​</a></h2><h3 id="市盈率-p-e" tabindex="-1">市盈率（P/E） <a class="header-anchor" href="#市盈率-p-e" aria-label="Permalink to &quot;市盈率（P/E）&quot;">​</a></h3><p><strong>计算公式：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>市盈率 = 股价 / 每股收益</span></span></code></pre></div><p><strong>类型：</strong></p><ul><li>静态市盈率（TTM）</li><li>动态市盈率（预期）</li><li>滚动市盈率</li></ul><p><strong>适用情况：</strong></p><ul><li>盈利稳定的企业</li><li>成熟型企业</li><li>同行业比较</li></ul><p><strong>局限性：</strong></p><ul><li>亏损企业无法使用</li><li>周期性企业误判风险</li><li>会计政策差异影响</li></ul><h3 id="市净率-p-b" tabindex="-1">市净率（P/B） <a class="header-anchor" href="#市净率-p-b" aria-label="Permalink to &quot;市净率（P/B）&quot;">​</a></h3><p><strong>计算公式：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>市净率 = 股价 / 每股净资产</span></span></code></pre></div><p><strong>适用情况：</strong></p><ul><li>金融企业</li><li>重资产企业</li><li>周期性企业</li></ul><p><strong>优势：</strong></p><ul><li>净资产相对稳定</li><li>适合资产密集型行业</li><li>波动相对较小</li></ul><p><strong>注意事项：</strong></p><ul><li>净资产质量差异</li><li>无形资产影响</li><li>会计政策差异</li></ul><h3 id="市销率-p-s" tabindex="-1">市销率（P/S） <a class="header-anchor" href="#市销率-p-s" aria-label="Permalink to &quot;市销率（P/S）&quot;">​</a></h3><p><strong>计算公式：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>市销率 = 总市值 / 营业收入</span></span></code></pre></div><p><strong>适用情况：</strong></p><ul><li>成长型企业</li><li>亏损企业</li><li>周期性企业底部</li></ul><p><strong>优势：</strong></p><ul><li>收入相对稳定</li><li>不受盈利波动影响</li><li>适合不同会计准则比较</li></ul><p><strong>局限性：</strong></p><ul><li>忽略盈利能力</li><li>不适合利润率差异大的行业</li></ul><h3 id="ev-ebitda" tabindex="-1">EV/EBITDA <a class="header-anchor" href="#ev-ebitda" aria-label="Permalink to &quot;EV/EBITDA&quot;">​</a></h3><p><strong>计算公式：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>EV/EBITDA = 企业价值 / 息税折旧摊销前利润</span></span></code></pre></div><p><strong>企业价值（EV）计算：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>EV = 市值 + 总负债 - 现金</span></span></code></pre></div><p><strong>适用情况：</strong></p><ul><li>跨行业比较</li><li>资本结构差异大的企业</li><li>国际比较</li></ul><p><strong>优势：</strong></p><ul><li>消除资本结构影响</li><li>消除折旧摊销影响</li><li>适合并购估值</li></ul><h2 id="可比公司选择" tabindex="-1">可比公司选择 <a class="header-anchor" href="#可比公司选择" aria-label="Permalink to &quot;可比公司选择&quot;">​</a></h2><h3 id="选择标准" tabindex="-1">选择标准 <a class="header-anchor" href="#选择标准" aria-label="Permalink to &quot;选择标准&quot;">​</a></h3><p><strong>行业相似性：</strong></p><ul><li>主营业务相同</li><li>商业模式相似</li><li>客户群体重叠</li></ul><p><strong>规模相似性：</strong></p><ul><li>营业收入规模相近</li><li>资产规模相近</li><li>市场地位相似</li></ul><p><strong>成长性相似：</strong></p><ul><li>历史增长率相近</li><li>未来增长预期相似</li><li>生命周期阶段相近</li></ul><p><strong>风险特征相似：</strong></p><ul><li>财务风险水平</li><li>经营风险特征</li><li>市场风险暴露</li></ul><h3 id="可比公司数据库" tabindex="-1">可比公司数据库 <a class="header-anchor" href="#可比公司数据库" aria-label="Permalink to &quot;可比公司数据库&quot;">​</a></h3><p><strong>国内市场：</strong></p><ul><li>同行业上市公司</li><li>科创板/创业板企业</li><li>新三板企业</li></ul><p><strong>国际市场：</strong></p><ul><li>海外同行业公司</li><li>新兴市场公司</li><li>发达市场公司</li></ul><p><strong>数据库来源：</strong></p><ul><li>Wind数据库</li><li>Bloomberg终端</li><li>各大券商研报</li></ul><h2 id="相对估值方法" tabindex="-1">相对估值方法 <a class="header-anchor" href="#相对估值方法" aria-label="Permalink to &quot;相对估值方法&quot;">​</a></h2><h3 id="算术平均法" tabindex="-1">算术平均法 <a class="header-anchor" href="#算术平均法" aria-label="Permalink to &quot;算术平均法&quot;">​</a></h3><p><strong>步骤：</strong></p><ol><li>选择可比公司</li><li>计算各公司估值指标</li><li>计算算术平均值</li><li>应用到目标公司</li></ol><p><strong>优缺点：</strong></p><ul><li>简单易用</li><li>受极端值影响大</li><li>未考虑公司差异</li></ul><h3 id="加权平均法" tabindex="-1">加权平均法 <a class="header-anchor" href="#加权平均法" aria-label="Permalink to &quot;加权平均法&quot;">​</a></h3><p><strong>权重考虑：</strong></p><ul><li>市值权重</li><li>收入权重</li><li>主观判断权重</li></ul><p><strong>公式：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>加权平均估值 = Σ(估值i × 权重i)</span></span></code></pre></div><h3 id="回归分析法" tabindex="-1">回归分析法 <a class="header-anchor" href="#回归分析法" aria-label="Permalink to &quot;回归分析法&quot;">​</a></h3><p><strong>模型建立：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>估值 = a + b1×因素1 + b2×因素2 + ... + ε</span></span></code></pre></div><p><strong>常见因素：</strong></p><ul><li>盈利增长率</li><li>ROE水平</li><li>负债率</li><li>市场份额</li></ul><h2 id="历史估值比较" tabindex="-1">历史估值比较 <a class="header-anchor" href="#历史估值比较" aria-label="Permalink to &quot;历史估值比较&quot;">​</a></h2><h3 id="历史估值区间" tabindex="-1">历史估值区间 <a class="header-anchor" href="#历史估值区间" aria-label="Permalink to &quot;历史估值区间&quot;">​</a></h3><p><strong>分析内容：</strong></p><ul><li>历史估值波动范围</li><li>估值中枢变化趋势</li><li>极端值出现原因</li></ul><p><strong>应用价值：</strong></p><ul><li>判断当前估值水平</li><li>识别估值回归机会</li><li>理解估值驱动因素</li></ul><h3 id="估值驱动因素分析" tabindex="-1">估值驱动因素分析 <a class="header-anchor" href="#估值驱动因素分析" aria-label="Permalink to &quot;估值驱动因素分析&quot;">​</a></h3><p><strong>基本面因素：</strong></p><ul><li>盈利增长率</li><li>盈利质量</li><li>资产效率</li></ul><p><strong>市场因素：</strong></p><ul><li>流动性环境</li><li>风险偏好</li><li>行业景气度</li></ul><p><strong>政策因素：</strong></p><ul><li>监管政策变化</li><li>产业政策调整</li><li>宏观政策导向</li></ul><h2 id="跨市场估值比较" tabindex="-1">跨市场估值比较 <a class="header-anchor" href="#跨市场估值比较" aria-label="Permalink to &quot;跨市场估值比较&quot;">​</a></h2><h3 id="国际比较方法" tabindex="-1">国际比较方法 <a class="header-anchor" href="#国际比较方法" aria-label="Permalink to &quot;国际比较方法&quot;">​</a></h3><p><strong>汇率调整：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>调整后估值 = 原估值 × 汇率因子</span></span></code></pre></div><p><strong>会计准则调整：</strong></p><ul><li>IFRS vs GAAP vs 中国会计准则</li><li>会计政策差异调整</li><li>信息披露差异考虑</li></ul><p><strong>市场特征调整：</strong></p><ul><li>市场有效性差异</li><li>投资者结构差异</li><li>流动性差异</li></ul><h3 id="a-h股比较" tabindex="-1">A/H股比较 <a class="header-anchor" href="#a-h股比较" aria-label="Permalink to &quot;A/H股比较&quot;">​</a></h3><p><strong>溢价/折价分析：</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>A/H溢价率 = (A股价格 - H股价格×汇率) / (H股价格×汇率)</span></span></code></pre></div><p><strong>影响因素：</strong></p><ul><li>市场分割</li><li>投资者结构</li><li>流动性差异</li><li>信息不对称</li></ul><h2 id="相对估值应用场景" tabindex="-1">相对估值应用场景 <a class="header-anchor" href="#相对估值应用场景" aria-label="Permalink to &quot;相对估值应用场景&quot;">​</a></h2><h3 id="价值投资" tabindex="-1">价值投资 <a class="header-anchor" href="#价值投资" aria-label="Permalink to &quot;价值投资&quot;">​</a></h3><ul><li>寻找被低估股票</li><li>同行业轮动</li><li>小市值价值发现</li></ul><h3 id="成长投资" tabindex="-1">成长投资 <a class="header-anchor" href="#成长投资" aria-label="Permalink to &quot;成长投资&quot;">​</a></h3><ul><li>成长性调整后的估值</li><li>增长潜力比较</li><li>行业龙头溢价</li></ul><h3 id="事件驱动" tabindex="-1">事件驱动 <a class="header-anchor" href="#事件驱动" aria-label="Permalink to &quot;事件驱动&quot;">​</a></h3><ul><li>并购重组估值</li><li>重组对标分析</li><li>定增定价参考</li></ul><h2 id="相对估值局限性" tabindex="-1">相对估值局限性 <a class="header-anchor" href="#相对估值局限性" aria-label="Permalink to &quot;相对估值局限性&quot;">​</a></h2><h3 id="市场有效性问题" tabindex="-1">市场有效性问题 <a class="header-anchor" href="#市场有效性问题" aria-label="Permalink to &quot;市场有效性问题&quot;">​</a></h3><ul><li>市场情绪影响</li><li>泡沫和低估并存</li><li>市场非理性波动</li></ul><h3 id="可比公司选择困难" tabindex="-1">可比公司选择困难 <a class="header-anchor" href="#可比公司选择困难" aria-label="Permalink to &quot;可比公司选择困难&quot;">​</a></h3><ul><li>完全可比公司少</li><li>行业边界模糊</li><li>商业模式差异</li></ul><h3 id="估值指标适用性" tabindex="-1">估值指标适用性 <a class="header-anchor" href="#估值指标适用性" aria-label="Permalink to &quot;估值指标适用性&quot;">​</a></h3><ul><li>不同行业适用指标不同</li><li>同一指标不同时期表现不同</li><li>单一指标局限性大</li></ul><h2 id="提高相对估值准确性" tabindex="-1">提高相对估值准确性 <a class="header-anchor" href="#提高相对估值准确性" aria-label="Permalink to &quot;提高相对估值准确性&quot;">​</a></h2><h3 id="多指标结合" tabindex="-1">多指标结合 <a class="header-anchor" href="#多指标结合" aria-label="Permalink to &quot;多指标结合&quot;">​</a></h3><ul><li>综合使用多个估值指标</li><li>指标间相互验证</li><li>行业特色指标应用</li></ul><h3 id="动态调整" tabindex="-1">动态调整 <a class="header-anchor" href="#动态调整" aria-label="Permalink to &quot;动态调整&quot;">​</a></h3><ul><li>定期更新可比公司</li><li>调整权重和参数</li><li>跟踪估值变化趋势</li></ul><h3 id="质量调整" tabindex="-1">质量调整 <a class="header-anchor" href="#质量调整" aria-label="Permalink to &quot;质量调整&quot;">​</a></h3><ul><li>盈利质量差异调整</li><li>成长性差异调整</li><li>风险特征调整</li></ul><h2 id="投资策略结合" tabindex="-1">投资策略结合 <a class="header-anchor" href="#投资策略结合" aria-label="Permalink to &quot;投资策略结合&quot;">​</a></h2><h3 id="绝对估值-相对估值" tabindex="-1">绝对估值+相对估值 <a class="header-anchor" href="#绝对估值-相对估值" aria-label="Permalink to &quot;绝对估值+相对估值&quot;">​</a></h3><ul><li>相对估值找机会</li><li>绝对估值验价值</li><li>综合判断投资价值</li></ul><h3 id="定量-定性分析" tabindex="-1">定量+定性分析 <a class="header-anchor" href="#定量-定性分析" aria-label="Permalink to &quot;定量+定性分析&quot;">​</a></h3><ul><li>定量指标筛选</li><li>定性深度分析</li><li>综合投资决策</li></ul><h3 id="长期-短期视角" tabindex="-1">长期+短期视角 <a class="header-anchor" href="#长期-短期视角" aria-label="Permalink to &quot;长期+短期视角&quot;">​</a></h3><ul><li>长期价值判断</li><li>短期交易机会</li><li>组合策略设计</li></ul>',131)])])}const g=a(s,[["render",o]]);export{c as __pageData,g as default};
