import{_ as a,c as r,o as l,af as t}from"./chunks/framework.CpuDhtGM.js";const d=JSON.parse('{"title":"投资工具","description":"","frontmatter":{},"headers":[],"relativePath":"value-investing/value-investing/investment-tools.md","filePath":"value-investing/value-investing/investment-tools.md","lastUpdated":1765527108000}'),i={name:"value-investing/value-investing/investment-tools.md"};function n(o,e,h,s,c,u){return l(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="投资工具" tabindex="-1">投资工具 <a class="header-anchor" href="#投资工具" aria-label="Permalink to &quot;投资工具&quot;">​</a></h1><h2 id="文章推荐" tabindex="-1">文章推荐 <a class="header-anchor" href="#文章推荐" aria-label="Permalink to &quot;文章推荐&quot;">​</a></h2><ul><li><a href="https://mp.weixin.qq.com/s/LOjD3GTn_9wQUIMRfkPhbA" target="_blank" rel="noreferrer">我常用的投资工具推荐（20200616 终身黑白）</a></li></ul><h2 id="投资工具-1" tabindex="-1">投资工具 <a class="header-anchor" href="#投资工具-1" aria-label="Permalink to &quot;投资工具&quot;">​</a></h2><h3 id="年报下载" tabindex="-1">年报下载 <a class="header-anchor" href="#年报下载" aria-label="Permalink to &quot;年报下载&quot;">​</a></h3><ul><li><a href="http://www.cninfo.com.cn/" target="_blank" rel="noreferrer">A 股年报下载，巨潮资讯</a></li><li><a href="https://www.hkexnews.hk/index_c.htm" target="_blank" rel="noreferrer">港股年报下载，港交所官网（搜索关键字要用繁体）</a></li></ul><h3 id="问财" tabindex="-1">问财 <a class="header-anchor" href="#问财" aria-label="Permalink to &quot;问财&quot;">​</a></h3><ul><li><a href="http://www.iwencai.com/" target="_blank" rel="noreferrer">问财（同花顺旗下投研平台）</a></li><li>方便搜索数据，如“洋河股份近十年净利润”、 “养猪概念股”</li></ul><h3 id="理杏仁" tabindex="-1">理杏仁 <a class="header-anchor" href="#理杏仁" aria-label="Permalink to &quot;理杏仁&quot;">​</a></h3><ul><li><a href="https://www.lixinger.com" target="_blank" rel="noreferrer">理杏仁</a></li><li>用的时候 1 元钱买 2 小时</li><li>查历史 PB、PE，还可以查企业和行业的各种估值，和财务指标数据</li><li><a href="http://biz.finance.sina.com.cn/company/compare/compare.php?stock_code=002304" target="_blank" rel="noreferrer">新浪财经</a></li><li>简单查数据，把网址最后六个数字替换成想要查询的公司</li></ul><h3 id="芝士财富" tabindex="-1">芝士财富 <a class="header-anchor" href="#芝士财富" aria-label="Permalink to &quot;芝士财富&quot;">​</a></h3><p>一款选股 APP，可以用各种条件筛选股票，进入个股页面还可以查看，股票优势、股票风险、相关研报等数据，功能很丰富。</p><h3 id="大宗商品" tabindex="-1">大宗商品 <a class="header-anchor" href="#大宗商品" aria-label="Permalink to &quot;大宗商品&quot;">​</a></h3><ul><li><a href="http://www.100ppi.com/" target="_blank" rel="noreferrer">生意社</a></li><li><a href="https://www.sci99.com/" target="_blank" rel="noreferrer">卓创资讯</a></li><li>大宗商品价格查询，如果你买大宗商品类的公司，比如能源、化工、塑胶、农产品等等，你一定需要用他来观察产品价格变动。</li><li>也有付费功能，我们普通投资者基本用免费功能就能满足需要了。</li></ul><h3 id="天天基金网" tabindex="-1">天天基金网 <a class="header-anchor" href="#天天基金网" aria-label="Permalink to &quot;天天基金网&quot;">​</a></h3><ul><li><a href="https://1234567.com.cn/" target="_blank" rel="noreferrer">天天基金网</a></li><li>基金有哪些成分股，经理介绍，指数基金具体跟踪什么指数，历史收益，一切和基金相关数据都可以在这里查询。</li></ul><h3 id="集思录" tabindex="-1">集思录 <a class="header-anchor" href="#集思录" aria-label="Permalink to &quot;集思录&quot;">​</a></h3><ul><li><a href="https://www.jisilu.cn/" target="_blank" rel="noreferrer">集思录</a></li><li>低风险投资者聚集地，分级基金、可转债、套利交易、逆回购相关数据都可以在这里查询。</li></ul><h3 id="国证指数" tabindex="-1">国证指数 <a class="header-anchor" href="#国证指数" aria-label="Permalink to &quot;国证指数&quot;">​</a></h3><ul><li><a href="http://www.cnindex.com.cn/" target="_blank" rel="noreferrer">国证指数</a></li><li>可以免费查看行业当前估值。</li></ul><h3 id="其它" tabindex="-1">其它 <a class="header-anchor" href="#其它" aria-label="Permalink to &quot;其它&quot;">​</a></h3><ul><li>时时新闻 <ul><li><a href="https://www.cls.cn/" target="_blank" rel="noreferrer">财联社</a></li><li><a href="https://www.yuncaijing.com/" target="_blank" rel="noreferrer">云财经</a></li></ul></li><li><a href="http://www.guabu.com/tools/fuli.htm" target="_blank" rel="noreferrer">复利计算器</a></li><li><a href="http://data.eastmoney.com/hsgt/index.html" target="_blank" rel="noreferrer">陆港通南北资金流向</a></li></ul>',22)])])}const m=a(i,[["render",n]]);export{d as __pageData,m as default};
